import React from 'react';
import SpaceInfoComponent from './SpaceInfoComponent';
class FilterPanel extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            PanelList: []
        }   
    }
    componentDidMount() {
        this.spaceXData();
    }
    spaceXData = () => {
        fetch('https://api.spacexdata.com/v3/launches?limit=100')
        .then(response => response.json())
        .then(json => {
            this.setState({
                PanelList: json
        })
        console.log("PanelList",this.state.PanelList);
        })
    }
    clickOnYearButton = () => { 
        const show = this.state.PanelList.filter(name => name.launch_year === '2014');
        console.log("clickOnYearButton",show);
    }
    render() {
        const { PanelList } = this.state;
        return<>
        <main className="main clearfix">
            <div className="filter">
                    <h3>Filters</h3>
                    <div className="filterinnerWrap">
                        <span>Launch Year</span>
                        <ul>
                            <li onClick={this.clickOnYearButton}>2006</li>
                            <li>2007</li>
                            <li>2008</li>
                            <li>2009</li>
                            <li>2010</li>
                            <li>2011</li>
                            <li>2012</li>
                            <li>2013</li>
                            <li>2014</li>
                            <li>2015</li>
                            <li>2016</li>
                            <li>2017</li>
                            <li>2018</li>
                            <li>2019</li>
                            <li>2020</li>
                        </ul>
                    </div>
                    <div className="filterinnerWrap">
                        <span>Successful Launch</span>
                        <ul>
                            <li>true</li>
                            <li>false</li>
                        </ul>
                    </div>
                    <div className="filterinnerWrap">
                        <span>Successful Landing</span>
                        <ul>
                            <li>true</li>
                            <li>false</li>
                        </ul>
                    </div>
            </div>
            <div className="programList">
                <ul>

                    {
                        PanelList.map((data,key)=>{
                            return(
                                <SpaceInfoComponent data={data} key={key}/>
                            )
                        })
                    }
                </ul>
            </div>
        </main>

        </>
    }
}
export default FilterPanel;
