import React from 'react';
import SpaceInfoComponent from './SpaceInfoComponent';
class FilterPanel extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            originalDataSaved : [],
            PanelList: []
        }   
    }
    componentDidMount() {
        this.spaceXData();
    }
    spaceXData = () => {
        fetch('https://api.spacexdata.com/v3/launches?limit=100')
        .then(response => response.json())
        .then(json => {
            this.setState({
                PanelList: json,
                originalDataSaved:json
        })
        console.log("PanelList",this.state.PanelList);
        })
    }
    clickOnYearButton = (getYear) => { 
        console.log("getYear==",getYear);
        const filteredResultOfYear = this.state.originalDataSaved.filter(name => name.launch_year === getYear);
        console.log("clickOnYearButton",filteredResultOfYear);
        this.setState({
            PanelList: filteredResultOfYear
        })
    }
    render() {
        const { PanelList } = this.state;
        return<>
        <main className="main clearfix">
            <div className="filter">
                    <h3>Filters</h3>
                    <div className="filterinnerWrap">
                        <span>Launch Year</span>
                        <ul>
                            <li onClick={() => this.clickOnYearButton('2006')}>2006</li>
                            <li onClick={() => this.clickOnYearButton('2007')}>2007</li>
                            <li onClick={() => this.clickOnYearButton('2008')}>2008</li>
                            <li onClick={() => this.clickOnYearButton('2009')}>2009</li>
                            <li onClick={() => this.clickOnYearButton('2010')}>2010</li>
                            <li onClick={() => this.clickOnYearButton('2011')}>2011</li>
                            <li onClick={() => this.clickOnYearButton('2012')}>2012</li>
                            <li onClick={() => this.clickOnYearButton('2013')}>2013</li>
                            <li onClick={() => this.clickOnYearButton('2014')}>2014</li>
                            <li onClick={() => this.clickOnYearButton('2015')}>2015</li>
                            <li onClick={() => this.clickOnYearButton('2016')}>2016</li>
                            <li onClick={() => this.clickOnYearButton('2017')}>2017</li>
                            <li onClick={() => this.clickOnYearButton('2018')}>2018</li>
                            <li onClick={() => this.clickOnYearButton('2019')}>2019</li>
                            <li onClick={() => this.clickOnYearButton('2020')}>2020</li>
                        </ul>
                    </div>
                    <div className="filterinnerWrap">
                        <span>Successful Launch</span>
                        <ul>
                            <li>true</li>
                            <li>false</li>
                        </ul>
                    </div>
                    <div className="filterinnerWrap">
                        <span>Successful Landing</span>
                        <ul>
                            <li>true</li>
                            <li>false</li>
                        </ul>
                    </div>
            </div>
            <div className="programList">
                <ul>

                    { PanelList.length > 0 ?
                        PanelList.map((data,key)=>{
                            return(
                                <SpaceInfoComponent data={data} key={key}/>
                            )
                        }) : <div className="no-match">No Matches Found</div>
                    }

                </ul>
            </div>
        </main>

        </>
    }
}
export default FilterPanel;
